(130, 7, 1, 1, '2017-2018', '<p>A l\'aide d\'au moins 3 cam&eacute;ra essaye d\'effectuer un montage en multicam &agrave; l\'aide de Premiere.</p>\r\n<h4>Remises</h4>\r\n<ul>\r\n<li>Un export du travail termin&eacute;</li>\r\n<li>Le projet .pproj dans le dossier MASTER de la cin&eacute;mat&egrave;que</li>\r\n</ul>', '2018-05-17', '2018-06-15', '2018-06-15', '[TECHNO] Multicam', 1, 0, 'Expérimente la technique du MultiCam et tires-en les leçons!', 4, 'mp4', 1, 0),
(131, 1, 1, 1, '2017-2018', '<p>Remets:</p>\r\n<ul>\r\n<li>une photo d\'identit&eacute; (effectu&eacute;e en studio), comme demand&eacute;e pour la carte d\'identit&eacute;.</li>\r\n<li>un portrait cr&eacute;atif du m&eacute;me mod&egrave;le, technique libre.</li>\r\n</ul>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>', '2018-05-17', '2018-06-07', '2018-06-07', 'Photo d\'identité VS Portrait', 1, 0, 'Montre le différence entre une photo de photomaton et celle d\'un portraitiste', 4, 'jpg', 2, 0),
