(229, 2, 1, 1, '2020-2021', NULL, '2020-10-08', '2020-10-08', '2020-10-08', 'Fonctionnement de l\'APN', 1, 1, 'Évaluation écrite', 1, NULL, 0, 0),
(230, 1, 1, 1, '2020-2021', '<p>A l\'aide des r&eacute;glages suivants, r&eacute;alise plusieurs photographies en lightpainting:</p><p>- Les ISO r&eacute;gl&eacute;s sur 800<br />- Le temps de pose sur Bulb<br />- Le diaphragme sera utilis&eacute; pour l\'exposition</p><p>&nbsp;</p><p>Ta photographie devra ressembler &agrave; ceci (couleur, fond, taille), mais avec ton pr&eacute;nom:</p><p><img src=\"https://s14-eu5.startpage.com/cgi-bin/serveimage?url=https%3A%2F%2Fi.ytimg.com%2Fvi%2FlSYkNiBrhYI%2Fmaxresdefault.jpg&amp;sp=95f980798e4d8309ee275e7e33253a02\" alt=\"\" width=\"359\" height=\"202\" /></p><p>&nbsp;</p>', '2020-09-13', '2020-10-30', '2020-10-09', 'Lightpainting - Ecriture', 1, 0, 'Allons un peu plus loin dans le lightpainting!', 1, 'jpg', 1, 1),
(231, 1, 1, 1, '2020-2021', '<p>A l\'aide des r&eacute;glages suivants, r&eacute;alise plusieurs photographies en lightpainting:</p><p>- Les ISO r&eacute;gl&eacute;s sur 800<br />- Le temps de pose sur Bulb<br />- Le diaphragme sera utilis&eacute; pour l\'exposition</p><p>Contrainte: Ta photographie devra reproduire aussi fid&egrave;lement que possible la photographie en exemple.</p><p>&nbsp;</p><p>&nbsp;</p>', '2020-09-20', '2020-10-30', '2020-10-09', 'Lightpainting - Boule de feu', 1, 0, 'Allons un peu plus loin dans le lightpainting!', 1, 'jpg', 1, 1),
(232, 1, 1, 1, '2020-2021', '<p>A l\'aide des r&eacute;glages suivants, r&eacute;alise plusieurs photographies en lightpainting:</p><p>- Les ISO r&eacute;gl&eacute;s sur 800<br />- Le temps de pose sur Bulb<br />- Le diaphragme sera utilis&eacute; pour l\'exposition</p><p>Contrainte: Ta photographie devra contenir au moins: 1 coup de flash et une intervention de lightpainting.</p><p>Remet ensuite <strong>la meilleure photographie</strong>&nbsp; que tu as r&eacute;alis&eacute;.</p><p><strong>Soit cr&eacute;atif et n\'aie pas peur d\'explorer les techniques!</strong></p><h3>Pour aller plus loin</h3><p>Plus d\'informations sur la technique: <a href=\"https://fr.wikipedia.org/wiki/Light_painting\">https://fr.wikipedia.org/wiki/Light_painting</a></p><p><iframe src=\"//player.vimeo.com/video/142250486?title=0&amp;byline=0\" width=\"425\" height=\"350\"></iframe></p>', '2020-09-27', '2020-10-30', '2020-10-09', 'Lightpainting - Création', 1, 0, 'Tu viens de découvrir des photographies en lightpainting, à ton tour maintenant d\'utiliser la technique!', 1, 'jpg', 1, 1),
